<script>
  import {orderSave, orderInfo} from '~/api/order.js'
  import QRCode from 'qrcode'
  export default {
    props: {
      data: {
        type: [Object, Array],
        default: null
      },
      no: {
        type: String,
        default: ''
      },
      isCourse: {
        type: Boolean
      }
    },
    data () {
      return {
        isVip: false,
        btntext: '下一步',
        checkPay: false,
        userInfo: this.$store.state.userInfo,
        courseData: '',
        order: {
          channelType: 1,
          courseId: 0,
          payType: 1,
          remarkCus: ''
        },
        orderInfo: null,
        payStep: 0,
        player: null
      }
    },
    mounted () {
      // this.qrcode('weixin://wxpay/bizpayurl?pr=GlkCzqF');
      console.log(this.$router)
      this.order.courseId = this.data.course.id;
      this.courseData = this.data.course;
      console.log(this.courseData)
    },
    methods: {
      reload () {
        window.location.reload();
      },
      close (event) {
        this.checkPay = true;
        this.$emit('hidefun', event);
      },
      sel (cno) {
        this.checkPay = true;
        this.$emit('btnClick', cno)
      },
      qrcode (url) {
        QRCode.toCanvas(document.getElementById('canvas'), url, {
          width: 180,
          height: 180
        }, function (error) {
          if (error) console.error(error)
          console.log('success!')
        })
      },
      startView () {

      },
      submit () {
        let that = this;
        that.btntext = '正在提交...';
        console.log(that.order)
        orderSave(that.order).then(res => {
          res = res.data;
          console.log(res)
          that.btntext = '下一步';
          if (res.code === 200) {
            that.payStep = 1;
            that.orderInfo = res.data;
            this.ocl = setTimeout(function () {
              that.qrcode(res.data.payMessage);
            }, 100);
            that.getOrderInfo(res.data.orderNo)
          } else if (res.code >= 300 && res.code < 400) {
            this.$msgBox({
              content: res.msg,
              isShowCancelBtn: false
            }).then(() => {
              this.$store.dispatch('REDIRECT_LOGIN');
            }).catch(() => {})
          } else {
            this.$msgBox({
              content: res.msg,
              isShowCancelBtn: false
            }).catch(() => {})
          }
        }).catch(() => {
          this.$msgBox({
            content: '提交失败,请重试',
            isShowCancelBtn: false
          })
          that.btntext = '重新提交';
        })
      },
      getOrderInfo (no) {
        let that = this;
        if (this.checkPay) {
          return false;
        }
        orderInfo({orderNo: no}).then(res => {
          res = res.data
          console.log(res)
          if (res.data.orderStatus === 1) {
            setTimeout(function () {
              that.getOrderInfo(no);
            }, 1000);
          } else if (res.data.orderStatus === 2) {
            if (that.order.courseType === 3) {
              this.close();
              this.$msgBox({
                content: '购买成功',
                isShowCancelBtn: false
              }).then(res => {
                window.location.reload();
              }).catch(() => {
                window.location.reload();
              })
            } else {
              that.payStep = 2;
            }
          } else {
            that.payStep = 3;
          }
        })
      }
    }
  }
</script>
